ALTER TABLE configuration_settings ALTER COLUMN value TYPE varchar(2000);

DELETE FROM configuration_settings where key = 'VENDOR_MAINTENANCE_REQUEST_EMAIL_TEMPLATE';
insert into configuration_settings (key, name, groupname, description, value, valueType,displayOrder)
	values ('VENDOR_MAINTENANCE_REQUEST_EMAIL_TEMPLATE', 'Pending maintenance notification template','Notification - Email','This template is used when sending Email notification when there is pending maintenance request','<p>Dear ${model.user.firstName} ${model.user.lastName}</p><p>Please note that there is a pending maintenance request for your organization on behalf of <b><i>${model.facility.name}</i></b> in <b><i>${model.facility.geographicZone.name}</i></b>.&nbsp;</p><table><tbody><tr><td>Equipment</td><td><b>${model.equipment.equipment.name}</b></td></tr><tr><td>Model</td><td><b>${model.equipment.model}</b></td></tr><tr><td>Request Reason</td><td>${model.request.reason}</td></tr><tr><td>Requested Date</td><td>${model.request.requestDate}</td></tr><tr><td>Recommended Date</td><td>${model.request.recommendedDate}</td></tr><tr><td>Comment</td><td>${model.request.comment}</td></tr></tbody></table><p><span><br>Thank you for your timely response.</span><br></p><p style="text-align: center;"><span style="color: #dfdfdf;">This email is sent to you because you were registered in the system as a representative to ${model.vendor.name}.&nbsp;</span></p>', 'HTML', 40);
